mapper.select:
SELECT id,user_id,blog_id,content,thank,overt,publish_time,report,solved,valid
FROM echo
WHERE user_id =#{userId}
OR blog_id IN(
    SELECT id
    FROM blog
    WHERE user_id = #{userId}
)

mapper.update:
UPDATE echo e
SET e.overt = 1
WHERE e.id = #{echoId}
AND e.blog_id =(
    SELECT b.id,
    FROM blog b
    WHERE e.id = #{echoId}
    AND b.id = e.blog_id
    AND b.user_id = #{userId}
)
